import React from 'react';
import { hairServices, nailServices, hairRemovalServices } from '../servicesData';
import './ServicesPage.css';  // Assuming you'll create a CSS file for this

const ServicesPage = () => {
  // Function to group hair services
  const groupHairServices = () => {
    const cuts = hairServices.filter(service => service.type === 'Cut');
    const styling = hairServices.filter(service => service.type === 'Styling');
    const treatments = hairServices.filter(service => service.type === 'Treatment');
    return { cuts, styling, treatments };
  };

  const { cuts, styling, treatments } = groupHairServices();

  return (
    <div className="container services-page">
      <h1>Our Services</h1>

      {/* Hair Services */}
      <h2>Hair Services</h2>

      <h3>Cuts & Trims</h3>
      <ul>
        {cuts.map((service, index) => (
          <li key={index}>
            <span>{service.service}</span>
            <span className="price">{service.price}</span>
          </li>
        ))}
      </ul>

      <h3>Blow Drys & Styling</h3>
      <ul>
        {styling.map((service, index) => (
          <li key={index}>
            <span>{service.service}</span>
            <span className="price">{service.price}</span>
          </li>
        ))}
      </ul>

      <h3>Treatments</h3>
      <ul>
        {treatments.map((service, index) => (
          <li key={index}>
            <span>{service.service}</span>
            <span className="price">{service.price}</span>
          </li>
        ))}
      </ul>

      {/* Nail Services */}
      <h2>Nail Services</h2>

      <h3>Manicure & Pedicure</h3>
      <ul>
        {nailServices.map((service, index) => (
          <li key={index}>
            <span>{service.service}</span>
            <span className="price">{service.price}</span>
          </li>
        ))}
      </ul>

      {/* Hair Removal Services */}
      <h2>Hair Removal Services</h2>

      <ul>
        {hairRemovalServices.map((service, index) => (
          <li key={index}>
            <span>{service.service}</span>
            <span className="price">{service.price}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ServicesPage;
